import React from "react";
import { useParams } from "react-router-dom";
import Footer from "../Components/Footer";
import Header from "../Components/Header";
import diacritics from "diacritics"; // Import diacritics for cleaning titles

const Details = ({ services }) => {
  const { title } = useParams();

  // Find the service based on the cleaned title (without diacritics)
  const service = services.find(
    (service) => diacritics.remove(service.title) === title
  );

  if (!service) {
    return <div>Service not found.</div>;
  }

  return (
    <>
      <Header background={true} title={service.title} />
      <main>
        <div className="details">
          <h2>{service.title}</h2>
          {service.text.map((paragraph, index) => (
            <p key={index}>{paragraph}</p>
          ))}
          <h3>{service.infoListTitle}</h3>
          <ul>
            {service.infoList.map((info, index) => (
              <li key={index}>{info}</li>
            ))}
          </ul>
          {/* You can add more components here to display other data like images */}
        </div>
      </main>
      <Footer />
    </>
  );
};

export default Details;
